package com.colin.wielga;

public class Straight {
	public int ystart;
	public int xstart;
	public int yend;
	public int xend;
	public int len;

	public Straight(int ystart, int xstart, int yend, int xend, int len) {
		// TODO Auto-generated constructor stub
		this.ystart = ystart;
		this.xstart= xstart;
		this.yend=yend;
		this.xend=xend;
		this.len=len;
	}

}